package week1.day1;

public class OddorEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 15;
		
		if(num1%2==0)
		{
			System.out.println("the number is even");
		}
		else
		{
			System.out.println("the number is odd");
		}

	}

}
